mapper.select:
SELECT MEMBER_ID, MEMBER_NAME
FROM MEMBER
WHERE MEMBER_ID = #{memberId} AND MEMBER_PASSWORD = #{memberPassword}

SELECT MNO, MNAME, EMAIL, CRE_DATE
FROM MEMBER
WHERE MNO = #{no}

mapper.update:
UPDATE MEMBER
MNAME = #{name}, EMAIL = #{email}, MOD_DATE = SYSDATE
WHERE MNO = #{no}

mapper.select:
SELECT IDX, STORED_FILE_NAME
FROM TB_FILE
WHERE PARENT_SEQ = #{parentSeq}
