mapper.select:
SELECT MEMBER_NUMBER, MEMBER_ID, MEMBER_PASSWORD, MEMBER_NAME, MEMBER_EMAIL, MEMBER_PHONE_NUMBER, MEMBER_GENDER, MEMBER_BIRTH_DATE, MEMBER_MODIFY_DATE, MEMBER_CREATE_DATE
FROM MEMBER
WHERE MEMBER_ID = #{memberId} AND MEMBER_PASSWORD = #{memberPassword}

SELECT MEMBER_ID
FROM MEMBER
WHERE MEMBER_EMAIL = #{memberEmail} AND MEMBER_PHONE_NUMBER = #{memberPhoneNumber}

SELECT MEMBER_PASSWORD
FROM MEMBER
WHERE MEMBER_ID = #{memberId} AND MEMBER_EMAIL = #{memberEmail}

mapper.update:
UPDATE MEMBER
MEMBER_NAME = #{memberName}, MEMBER_ID = #{memberId}, MEMBER_EMAIL = #{memberEmail}, MEMBER_PHONE_NUMBER = #{memberPhoneNumber}, MEMBER_BIRTH_DATE = #{memberBirthDate}, MEMBER_MODIFY_DATE = SYSDATE
WHERE MEMBER_PASSWORD = #{memberPassword}

mapper.select:
SELECT MEMBER_NUMBER, MEMBER_ID, MEMBER_NAME, MEMBER_EMAIL, MEMBER_PHONE_NUMBER, MEMBER_BIRTH_DATE
FROM MEMBER
WHERE MEMBER_NUMBER = #{memberNumber}
