mapper.select:
SELECT MOVIE.MOVIE_NUMBER, MOVIE.MOVIE_TITLE, MOVIE.MOVIE_SUMMARY, MOVIE.MOVIE_RELEASE_DATE, MOVIE.MOVIE_RUNTIME, MOVIE.MOVIE_PRICE, MOVIE.MOVIE_POSTER, MOVIE.MOVIE_PREVIEW, MOVIE.GENRE_NAME
FROM(	SELECT ROWNUM AS RNUM, MOVIE.MOVIE_NUMBER, MOVIE.MOVIE_TITLE, MOVIE.MOVIE_SUMMARY, MOVIE.MOVIE_RELEASE_DATE, MOVIE.MOVIE_RUNTIME, MOVIE.MOVIE_PRICE, MOVIE.MOVIE_POSTER, MOVIE.MOVIE_PREVIEW, MOVIE.GENRE_NAME
				FROM(	SELECT M.MOVIE_NUMBER, M.MOVIE_TITLE, M.MOVIE_SUMMARY, M.MOVIE_RELEASE_DATE, M.MOVIE_RUNTIME, M.MOVIE_PRICE, M.MOVIE_POSTER, M.MOVIE_PREVIEW, G.GENRE_NAME
								FROM MOVIE M JOIN GENRE G
								ON M.GENRE_NUMBER = G.GENRE_NUMBER
								ORDER BY MOVIE_NUMBER DESC
							) MOVIE
			) MOVIE
WHERE MOVIE.RNUM BETWEEN #{start} AND #{end}

mapper.update:
UPDATE MOVIE
MOVIE_TITLE = #{movieTitle}, MOVIE_SUMMARY = #{movieSummary}, MOVIE_RELEASE_DATE = #{movieReleaseDate}, MOVIE_RUNTIME = #{movieRuntime}, MOVIE_PRICE = #{moviePrice}, MOVIE_POSTER = #{moviePoster}, MOVIE_PREVIEW = #{moviePreview}
WHERE MOVIE_NUMBER = #{movieNumber}

mapper.select:
SELECT LINEREVIEW.MEMBER_ID, LINEREVIEW.MOVIE_TITLE, LINEREVIEW.LINE_REVIEW_NUMBER, LINEREVIEW.LINE_REVIEW_CONTEXT
FROM(   SELECT ROWNUM AS RNUM, LINEREVIEW.MEMBER_ID, LINEREVIEW.MOVIE_TITLE, LINEREVIEW.LINE_REVIEW_NUMBER, LINEREVIEW.LINE_REVIEW_CONTEXT
            FROM(   SELECT M.MEMBER_ID, MV.MOVIE_TITLE, LR.LINE_REVIEW_NUMBER, LR.LINE_REVIEW_CONTEXT
                        FROM LINE_REVIEW LR JOIN MEMBER M
                        ON LR.MEMBER_NUMBER = M.MEMBER_NUMBER JOIN MOVIE MV
                        ON LR.MOVIE_NUMBER = MV.MOVIE_NUMBER
                        ORDER BY LINE_REVIEW_NUMBER DESC
                    ) LINEREVIEW
            ) LINEREVIEW
WHERE LINEREVIEW.RNUM BETWEEN #{start} AND #{end}

mapper.update:
UPDATE LINE_REVIEW

	LINE_REVIEW_CONTEXT = #{lineReviewContext},
	MEMBER_NUMBER = #{memberNumber},
	MOVIE_NUMBER = #{movieNumber}

WHERE LINE_REVIEW_NUMBER = #{lineReviewNumber}
